  SELECT  O.ORDER_NUM, M.MEMBER_NUM, P.PRODUCT_NUM,
        P.PRODUCT_NAME, P.THUM_SYS_FILENAME, OP.PRICE, OP.COUNT,P.DESCRIPTION,  
        OP.SALEPRICE, DS.DELIVERY_STATUS,
        O.DELIVERY_FEE, O.TOT_PRICE,  O. TOT_GOODSPRICE, PT.PT_DETAILNAME,
        O.ORDER_NAME, O.ORDER_PHONE, O.ORDER_DATE,  
        O.ORDER_ADDR, O.ORDER_ADDR_DETAIL, O.ORDER_PLACE, O.ORDER_REQUEST,
        PC.CONDITION, OP.REVIEW_STATUS, o.USAGE_AMOUNT, EU.AMOUNT
FROM ORDERS O, ORDER_PRODUCT OP, PRODUCT P, 
    MEMBER M, DELIVERY_STATUS DS, PAYMENT_TYPE PT, 
        PACKAGE_CONDITION PC, EMONEY_USAGE EU
WHERE   M.MEMBER_NUM = O.MEMBER_NUM AND 
        O.ORDER_NUM = OP.ORDER_NUM AND
        OP.PRODUCT_NUM = P.PRODUCT_NUM AND
        PT.PT_NUM = O.PT_NUM AND
        DS.DELIVERY_STATUS_NUM = O.DELIVERY_STATUS_NUM AND
        P.STORAGE_NUM = PC.STORAGE_NUM AND
